function setNewBlobName(formObject,blob,fileType) {
    var oldBlobName = blob.getName();
    var uniqueId = createUniqueId(formObject);
    //Logger.log('oldBlobName='+oldBlobName);
    blob.setName(uniqueId+"-"+fileType+"-"+oldBlobName);
    return blob;
}

