//Util functions

function getCurrentTimestamp() {
  var timezone = "GMT-4";
  var timestamp_format = "yyyy-MM-dd HH:mm:ss";
  var date = Utilities.formatDate(new Date(), timezone, timestamp_format);
  return date;
}


function getCellIndexByName(sheet,name,row, maxColumn) {

  var index = -1;


  //getRange(row, column, numRows, numColumns)
  var range = sheet.getRange(row, 1, 1, maxColumn);
  var values = range.getValues();


  for( var col = 1; col <= maxColumn; col++ ) {

     var range = sheet.getRange(row, col);
     var values = range.getValues();
     var value = values[0][0];

     //Logger.log(value.toString() + "?=" + name);

     if( value.toString() == name ) {
        index = col;
        break;
     }    

  }  

  //Logger.log(index); // the array index in which the result was found, cc+1 is the Row index

  return index;
}


function Trim(string) {
  if( !string || string == null || string == "" ) {
    Logger.log("string to trim is invalid, string="+string);
    return "";
  }
  return string.replace(/\s/g, ""); 
}
